sys_script_fix.script:
var noCodeLog = "Fix Script: Set office building codes from names run\n\nThe following office locations did not have a code in the name, thus Buildnig Code was not set:";

var ofc = new GlideRecord('x_nike2_location_office');
ofc.addQuery('u_active', true);
ofc.addQuery('building_code', '');
//ofc.setLimit(10);
ofc.query();

while(ofc.next()){

	var name = ofc.name;
	if(name.indexOf("(") != -1){
		var bldgCode = name.substring(name.indexOf("(") + 1, name.indexOf(")"));
		ofc.building_code = bldgCode.trim();
		ofc.setWorkflow(false);
		ofc.update();
	}else{
		noCodeLog += "\n - " + name;
	}

	gs.info(noCodeLog);
}
