sys_transform_entry.source_script:
answer = (function transformEntry(source) {

	// Add your code here

var t = source.u_location_type.toString().trim();
var c = '';
	if ( t == 'u_neighborhood' || t == 'Neighborhood' )
		c = 'x_nike2_location_neighborhood';
	else if ( t == 'u_campus' || t == 'Campus' 
		c = 'x_nike2_location_campus';
	else if ( t == 'u_sub_management_country' || t == 'Sub Management Country' )
		c = 'x_nike2_location_sub_management_country';
	else if ( t == 'u_management_country' || t == 'Management Country' )
		c = 'x_nike2_location_management_country';
	else if ( t == 'u_sub_territory' || t == 'Sub Territory' )
		c = 'x_nike2_location_sub_territory';
	else if ( t == 'u_territory' || t == 'Territory' )
		c = 'x_nike2_location_territory';
	else if ( t == 'u_sub_geography' || t == 'Sub Geography' )
		c = 'x_nike2_location_sub_geography';
	else if ( t == 'u_geography' || t == 'Geography' )
		c = 'x_nike2_location_geography';
	else if ( t == 'u_super_geography' || t == 'Super Geography' )
		c = 'x_nike2_location_super_geography';
	else if ( t == 'u_global' || t == 'Global' )
		c = 'x_nike2_location_global';
gs.info( 'foo' );
gs.log( t + ' : ' + c, 'zzz' );
if ( c == '' )
ignore = true;
//gs.info( '2 : ' + source.u_location_type + ' : ' + source.u_location_name + ' : ' + source.u_sla_region );


	return c; // return the value to be put into the target field

})(source);
